STATIONS_BY_DB_ID = {s["db_id"]: s for s in STATIONS}
STATIONS_BY_MVG_ID = {s["mvg_id"]: s for s in STATIONS}


def get_by_db_id(db_id):
    return STATIONS_BY_DB_ID.get(db_id)


def get_by_mvg_id(mvg_id):
    return STATIONS_BY_MVG_ID.get(mvg_id)


def get_by_id(id: int):
    if id < 9999:
        # Passed id is in the MVG format.
        return STATIONS_BY_MVG_ID.get(id)
    else:
        # Passed id is the DB format.
        return STATIONS_BY_DB_ID.get(id)
